(58, 1, '2018-01-05 15:53:32', '2018-01-05 15:53:32', '<section id="bg_weddings" class="sub_header"><div class="sub_header_content"><div class="animated fadeInDown"><h1>Same Sex Weddings</h1><p>We are very pleased and excited to host same sex weddings</p></div></div></section>\r\n<div id="position"><div class="container"><ul><li><a href="/">Home</a></li><li><a href="undefined">Weddings</a></li><li>Same Sex Weddings</li></ul></div></div>\r\n<div class="container margin_60"><div class="main_title"><span></span><h2>Same sex weddings welcome at Downham Hall</h2><p class="lead">All are welcome to be married at our venue</p></div><div class="row"><div class="col-md-9"></div></div><div class="row"><div class="col-md-12"><p>We just wanted to confirm that we are welcoming all to Downham Hall including all from LGBTQ community. </p><p> \r\nSome venues may not be so forthcoming but as a family run venue we think it is important our children know we are very supportive of all marriages.</p></div></div></div>', 'Same Sex Marriages', '', 'inherit', 'closed', 'closed', '', '56-revision-v1', '', '', '2018-01-05 15:53:32', '2018-01-05 15:53:32', '', 56, 'https://www.downhamhall.com/2018/01/05/56-revision-v1/', 0, 'revision', '', 0),
(59, 1, '2018-01-05 15:54:13', '2018-01-05 15:54:13', '<section id="bg_general" class="sub_header"></section>\r\n<div id="position"><div class="container"><ul><li><a href="/">Home</a></li><li>Rooms</li></ul></div></div>\r\n<div class="container margin_60"><div class="main_title"><span></span><h2>Rooms information coming soon</h2><p class="lead">We are renovating our individual rooms right now and will have more information just as soon as we can!</p><p class="lead">In the mean time we have an Airbnb listing for a private room in the South Wing you can hire on Airbnb.<a target="_blank" href="https://www.airbnb.co.uk/rooms/17852538">&nbsp;See here. &nbsp;</a></p></div></div>', 'Rooms', '', 'publish', 'closed', 'closed', '', 'rooms', '', '', '2018-01-05 15:54:56', '2018-01-05 15:54:56', '', 0, 'https://www.downhamhall.com/?page_id=59', 0, 'page', '', 0),
